package practical_task1;

public class Operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b=5;
		int add=a+b;
		System.out.println("the sum by  + operators :"+add);
		int sub=a-b;
		System.out.println("the sub by - operators :"+sub);
		int multi=a*b;
		System.out.println("the multiply by * operators :"+multi);
		int div=a/b;
		System.out.println("the divied by / operators :"+div);
		int are=a|b;
		System.out.println("the or by || operators :"+are);
		int and=a&b;
		System.out.println("the and by & operators :"+and);
	}

}
